DROP VIEW IF EXISTS `tv_provinces`;
CREATE VIEW `tv_provinces` AS select `tb_provinces`.`id` AS `id`,`tb_provinces`.`provinceid` AS `provinceid`,`tb_provinces`.`province` AS `province`,`tb_provinces`.`longitude` AS `longitude`,`tb_provinces`.`latitude` AS `latitude`,now() AS `updated_date`,now() AS `created_date` from `tb_provinces`;
DROP VIEW IF EXISTS `tv_cities`;
CREATE VIEW `tv_cities` AS select `tb_cities`.`id` AS `id`,`tb_provinces`.`provinceid` AS `provinceid`,`tb_provinces`.`province` AS `province`,`tb_cities`.`cityid` AS `cityid`,`tb_cities`.`city` AS `city`,`tb_cities`.`longitude` AS `longitude`,`tb_cities`.`latitude` AS `latitude`,concat(`tb_provinces`.`province`,`tb_cities`.`city`) AS `provincecity`,now() AS `updated_date`,now() AS `created_date` from (`tb_provinces` join `tb_cities` on((`tb_provinces`.`provinceid` = `tb_cities`.`provinceid`)));
DROP VIEW IF EXISTS `tv_areas`;
CREATE VIEW `tv_areas` AS select `tb_cities`.`id` AS `id`,`tb_provinces`.`provinceid` AS `provinceid`,`tb_provinces`.`province` AS `province`,`tb_cities`.`cityid` AS `cityid`,`tb_cities`.`city` AS `city`,`tb_areas`.`areaid` AS `areaid`,`tb_areas`.`area` AS `area`,`tb_areas`.`longitude` AS `longitude`,`tb_areas`.`latitude` AS `latitude`,concat(`tb_provinces`.`province`,`tb_cities`.`city`,`tb_areas`.`area`) AS `provincecityarea`,now() AS `updated_date`,now() AS `created_date` from ((`tb_provinces` join `tb_cities` on((`tb_provinces`.`provinceid` = `tb_cities`.`provinceid`))) join `tb_areas` on((`tb_cities`.`cityid` = `tb_areas`.`cityid`)));